import React, { useState } from 'react';
import './ExpenseItem.css';

const ExpensiveItem = ( props ) => {

    const {
        data
    } = props;

    const [ info, setInfo ] = useState( data )
    console.log(info)

    const expenseDate = new Date().toLocaleString()
    const expenseTitle = 'Car Insurance';
    const expeseAmount = 399.99

    const renderText = () => {
        return(
            <>
                {/* for( const[ index, value ] of info.entries() ) {
                    <div className="expense-item" key={ index }>
                        <div>{expenseDate}</div>

                        <div className="expense-item__description">
                            <h2>{expenseTitle }</h2>
                            <div className="expense-item__price">${expeseAmount}</div>
                        </div>
                    </div>
                } */}

                { info.map( (item) => {

                    return (
                        <div className="expense-item" key={item.id}>
                            <div>{item.date}</div>
                        </div>
                    )
                    
                })}
                
            </>
        )
    }

    return (  
        <>
            { renderText() }
        </>
    );
}
 
export default ExpensiveItem;
