import React, { useState, useEffect } from 'react';
import ExpenseDate from './ExpenseDate';
import './ExpenseItem.css';

const ExpensiveItem = ( props ) => {

    const {
        data
    } = props;

    var [ info, setInfo ] = useState( data )
    const [ filterData, setFilterData ] = useState('');
    
    //funcion para el boton
    const bottonFunction = (item) => {
        console.log(item)
        item.title = 'nuevo titulo'
    }

    useEffect(() => {

    }, [ info ])

    const renderText = () => {
        return(
            <>
                
                { info?.map( (item) => {
                    return (
                        <div >
                            <div className="expense-item" key={item.id}>
                                <ExpenseDate data={item} />

                                <div className="expense-item__description">
                                    <h2>{item.title}</h2>

                                    <div className="expense-item__price">{item.amount}</div>
                                </div>

                                <button
                                    type='button'
                                    onClick={ () => bottonFunction(item) }
                                >Change Title</button>
                            </div>
                        </div>
                    )
                })}
            </>
        )
    }

    return (  
        <>
            <div className="finder-container">
                <input
                    type="text"
                    value={ filterData }
                    onChange={ (e) => setFilterData(e.target.value) }
                    name="filter"
                />

                <label className="find-label">Find an Expense</label>
            </div>
            { renderText() }
        </>
    );
}
 
export default ExpensiveItem;

