import React, { useState } from 'react';
import ExpensiveItem from './components/ExepenseItem';
import './components/ExpenseItem.css';
import NewExpense from './components/NewExpense/NewExpense';
import FilterExpense from './components/FilterComponent/FilterExpense';

const App = () => {

  //State to filter by date
  const [ filter, setFilter ] = useState('')

  const expenses = [ 
    { id: 1, title: 'Create a protection robot', price: 399.99, date: new Date()},
    { id: 2, title: 'Create an AI', price: 107.02, date: new Date()},
    { id: 3, title: 'Create a rocket ', price: 205.02, date: new Date()},
    { id: 4, title: 'Build a mars colony', price: 907.02, date: new Date()},
    { id: 5, title: 'Going to the moon', price: 907.02, date: new Date()},
    { id: 6, title: 'Travel to the universe', price: 507.02, date: new Date()},
    { id: 7, title: 'Travel to the universe on a starship', price: 1000.00, date: new Date()}
  ]

  const addExpenseHandler = (expense) => {
    console.log('New expense added in the form', expense)
  }

  const selectedYearHandler = ( year ) => {
    setFilter(year)
  }

  return (  
    <>
      <div>
        <div className='expanse-main__container'>
          <div className='expense-container'>

            <NewExpense  
              onSaveExpanseItem={addExpenseHandler}
            />
              <div>
                <FilterExpense
                  onYearSelected={selectedYearHandler}
                />
              </div>
            <ExpensiveItem 
              title={ expenses[0].title}
              amount={ expenses[0].price}
              date={ expenses[0].date}
            />

            <ExpensiveItem 
              title={ expenses[1].title}
              amount={ expenses[1].price}
              date={ expenses[1].date}
            />

            <ExpensiveItem 
              title={ expenses[2].title}
              amount={ expenses[2].price}
              date={ expenses[2].date}
            />
            <ExpensiveItem 
              title={ expenses[3].title}
              amount={ expenses[3].price}
              date={ expenses[3].date}
            />

            <ExpensiveItem 
              title={ expenses[4].title}
              amount={ expenses[4].price}
              date={ expenses[4].date}
            />
              <ExpensiveItem 
              title={ expenses[5].title}
              amount={ expenses[5].price}
              date={ expenses[5].date}
            />

            <ExpensiveItem 
              title={ expenses[6].title}
              amount={ expenses[6].price}
              date={ expenses[6].date}
            />

          </div>
        </div>
      </div>
    </>
  );
}

export default App;
