import React, { useState } from 'react';
import ExpenseDate from './ExpenseDate';
import './ExpenseItem.css';

const ExpensiveItem = ( props ) => {

    const {
        data
    } = props;

    var [ info, setInfo ] = useState( data )
    const [ filterData, setFilterData ] = useState('');

    /*var filterDataMap = info.filter( (element) => {
        //console.log(element.title)
        for( let i = 0; i <= filterData.length; i++){
            console.log(filterData.toLowerCase().trim(), "inside the fuction to filter")
            if( element.title.toLowerCase()[i] === filterData.toLowerCase()[i]){
                console.log(element, "hjgjgjgj")
                return element
            }
        }
    })*/
    var newInfo = [];
    const testDataMap = () => {
        const findText = filterData.toLowerCase().trim()
        for( let element of info ){
            let title = element.title.toLowerCase().trim();
            //console.log(title, "objeto ")
            if( title.indexOf( findText ) !== -1){
                console.log(newInfo.push(element.title))
            }
        }
    }

    console.log('nueva data', newInfo)
    

    const renderText = () => {
        return(
            <>
                {testDataMap()}
                { info?.map( (item) => {
                    //console.log(filterDataMap, "Info if the finder is not loading")
                    return (
                        <div >
                            <div className="expense-item" key={item.id}>
                                <ExpenseDate data={item} />

                                <div className="expense-item__description">
                                    <h2>{item.title}</h2>

                                    <div className="expense-item__price">{item.amount}</div>
                                </div>
                            </div>
                        </div>
                    )
                })}
            </>
        )
    }

    return (  
        <>
            <div className="finder-container">
                <input
                    type="text"
                    value={ filterData }
                    onChange={ (e) => setFilterData(e.target.value) }
                    name="filter"
                />

                <label className="find-label">Find an Expense</label>
            </div>
            { renderText() }
        </>
    );
}
 
export default ExpensiveItem;

